#!/bin/bash\n\n# ðŸš€ WEM Dashboard - One-Click Setup Script\n# This script sets up your complete WEM Dashboard for testing\n\nset -e  # Exit on any error\n\necho \"ðŸŒŠ WEM Dashboard Setup Starting...\"\necho \"=====================================\"\necho \"\"\n\n# Colors for output\nGREEN='\\033[0;32m'\nBLUE='\\033[0;34m'\nYELLOW='\\033[1;33m'\nRED='\\033[0;31m'\nNC='\\033[0m' # No Color\n\n# Check if .NET is installed\ncheck_dotnet() {\n    if command -v dotnet &> /dev/null; then\n        DOTNET_VERSION=$(dotnet --version)\n        echo -e \"${GREEN}âœ… .NET ${DOTNET_VERSION} found${NC}\"\n        return 0\n    else\n        echo -e \"${YELLOW}âš ï¸  .NET not found. Please install .NET 8.0:${NC}\"\n        echo \"   ðŸ“¥ Download: https://dotnet.microsoft.com/download/dotnet/8.0\"\n        return 1\n    fi\n}\n\n# Check if Node.js is installed\ncheck_node() {\n    if command -v node &> /dev/null; then\n        NODE_VERSION=$(node --version)\n        echo -e \"${GREEN}âœ… Node.js ${NODE_VERSION} found${NC}\"\n        return 0\n    else\n        echo -e \"${YELLOW}âš ï¸  Node.js not found. Please install Node.js 18+:${NC}\"\n        echo \"   ðŸ“¥ Download: https://nodejs.org/\"\n        return 1\n    fi\n}\n\necho \"ðŸ” Checking prerequisites...\"\necho \"\"\n\n# Check prerequisites\nPREREQS_OK=true\n\nif ! check_dotnet; then\n    PREREQS_OK=false\nfi\n\nif ! check_node; then\n    PREREQS_OK=false\nfi\n\nif [ \"$PREREQS_OK\" = false ]; then\n    echo \"\"\n    echo -e \"${RED}âŒ Prerequisites missing. Please install the required software and run this script again.${NC}\"\n    exit 1\nfi\n\necho \"\"\necho \"ðŸš€ Starting WEM Dashboard setup...\"\necho \"\"\n\n# Setup frontend\necho -e \"${BLUE}ðŸ“¦ Installing frontend dependencies...${NC}\"\nif command -v bun &> /dev/null; then\n    echo \"Using Bun for faster installs...\"\n    bun install\nelse\n    npm install\nfi\n\necho \"\"\necho -e \"${BLUE}ðŸ”§ Setting up environment...${NC}\"\n\n# Create .env.local for frontend\ncat > .env.local << EOF\n# WEM Dashboard - Local Development Environment\nVITE_API_BASE_URL=http://localhost:5000/api\nVITE_ENABLE_MOCK_DATA=false\nVITE_APP_TITLE=WEM Dashboard - Local\nEOF\n\necho -e \"${GREEN}âœ… Frontend environment configured${NC}\"\n\n# Setup backend\necho \"\"\necho -e \"${BLUE}ðŸ—„ï¸  Setting up backend database...${NC}\"\n\ncd backend/src/WemDashboard.API\n\n# Restore backend dependencies\necho \"Installing .NET packages...\"\ndotnet restore\n\necho \"\"\necho -e \"${GREEN}âœ… Backend dependencies installed${NC}\"\necho \"\"\n\n# Build backend\necho -e \"${BLUE}ðŸ”¨ Building backend...${NC}\"\ndotnet build --configuration Release\n\necho \"\"\necho -e \"${GREEN}âœ… Backend built successfully${NC}\"\necho \"\"\n\n# Go back to root directory\ncd ../../../\n\necho \"ðŸŽ‰ Setup completed successfully!\"\necho \"=============================\"\necho \"\"\necho -e \"${GREEN}ðŸš€ Your WEM Dashboard is ready for testing!${NC}\"\necho \"\"\necho \"ðŸ“‹ Next steps:\"\necho \"\"\necho \"1. ðŸ–¥ï¸  Start the Backend:\"\necho \"   cd backend/src/WemDashboard.API\"\necho \"   dotnet run\"\necho \"\"\necho \"2. ðŸŒ Access Swagger API Docs:\"\necho \"   http://localhost:5000/swagger\"\necho \"\"\necho \"3. ðŸ” Test Login Credentials:\"\necho \"   Email: admin@wemdashboard.com\"\necho \"   Password: Admin123!\"\necho \"\"\necho \"4. ðŸŽ¨ Start Frontend (in new terminal):\"\necho \"   npm run dev\"\necho \"   # or bun dev\"\necho \"\"\necho \"5. ðŸŒŸ Access Dashboard:\"\necho \"   http://localhost:5173\"\necho \"\"\necho -e \"${YELLOW}ðŸ“– For detailed testing instructions, see: TESTING_GUIDE.md${NC}\"\necho \"\"\necho -e \"${GREEN}âœ¨ Happy testing!${NC}\"\n"